--- src/hooks/useLazyLoading.ts
+++ src/hooks/useLazyLoading.ts
@@ -0,0 +1,64 @@
+import { useEffect, useRef, useState } from 'react';
+
+interface UseLazyLoadingOptions {
+  rootMargin?: string;
+  threshold?: number;
+  triggerOnce?: boolean;
+}
+
+export const useLazyLoading = (options: UseLazyLoadingOptions = {}) => {
+  const {
+    rootMargin = '50px',
+    threshold = 0.1,
+    triggerOnce = true,
+  } = options;
+
+  const [isInView, setIsInView] = useState(false);
+  const [isLoaded, setIsLoaded] = useState(false);
+  const elementRef = useRef<HTMLElement>(null);
+
+  useEffect(() => {
+    const element = elementRef.current;
+    if (!element) return;
+
+    // Check if Intersection Observer is supported
+    if (!('IntersectionObserver' in window)) {
+      // Fallback for older browsers - load immediately
+      setIsInView(true);
+      return;
+    }
+
+    const observer = new IntersectionObserver(
+      (entries) => {
+        entries.forEach((entry) => {
+          if (entry.isIntersecting) {
+            setIsInView(true);
+            
+            if (triggerOnce) {
+              observer.unobserve(element);
+            }
+          } else if (!triggerOnce) {
+            setIsInView(false);
+          }
+        });
+      },
+      {
+        rootMargin,
+        threshold,
+      }
+    );
+
+    observer.observe(element);
+
+    return () => {
+      observer.unobserve(element);
+    };
+  }, [rootMargin, threshold, triggerOnce]);
+
+  return {
+    ref: elementRef,
+    isInView,
+    isLoaded,
+    setIsLoaded,
+  };
+};

--- src/components/LazyImage/index.tsx
+++ src/components/LazyImage/index.tsx
@@ -0,0 +1,93 @@
+import { useState, useCallback } from 'react';
+import { useLazyLoading } from '../../hooks/useLazyLoading';
+import './LazyImage.scss';
+
+interface LazyImageProps {
+  src: string;
+  alt: string;
+  className?: string;
+  placeholder?: string;
+  onLoad?: () => void;
+  onError?: () => void;
+  style?: React.CSSProperties;
+}
+
+export const LazyImage = ({
+  src,
+  alt,
+  className = '',
+  placeholder,
+  onLoad,
+  onError,
+  style,
+}: LazyImageProps) => {
+  const [imageLoaded, setImageLoaded] = useState(false);
+  const [imageError, setImageError] = useState(false);
+  const { ref, isInView } = useLazyLoading({
+    rootMargin: '100px', // Start loading 100px before element enters viewport
+    threshold: 0.1,
+    triggerOnce: true,
+  });
+
+  const handleImageLoad = useCallback(() => {
+    setImageLoaded(true);
+    onLoad?.();
+  }, [onLoad]);
+
+  const handleImageError = useCallback(() => {
+    setImageError(true);
+    onError?.();
+  }, [onError]);
+
+  return (
+    <div 
+      ref={ref} 
+      className={`lazy-image-container ${className}`}
+      style={style}
+    >
+      {/* Placeholder/Skeleton */}
+      {!imageLoaded && !imageError && (
+        <div className="lazy-image-placeholder">
+          {placeholder ? (
+            <img 
+              src={placeholder} 
+              alt="" 
+              className="placeholder-image"
+              aria-hidden="true"
+            />
+          ) : (
+            <div className="skeleton-placeholder">
+              <div className="skeleton-shimmer"></div>
+            </div>
+          )}
+        </div>
+      )}
+
+      {/* Error State */}
+      {imageError && (
+        <div className="lazy-image-error">
+          <svg width="32" height="32" viewBox="0 0 24 24" fill="none">
+            <path 
+              d="M21 5V4C21 2.9 20.1 2 19 2H5C3.9 2 3 2.9 3 4V5H21ZM19 7H5V19C5 20.1 5.9 21 7 21H17C18.1 21 19 20.1 19 19V7ZM9 8H11V18H9V8ZM13 8H15V18H13V8Z" 
+              fill="currentColor"
+              opacity="0.5"
+            />
+          </svg>
+          <span>Failed to load image</span>
+        </div>
+      )}
+
+      {/* Actual Image - Only load when in view */}
+      {isInView && !imageError && (
+        <img
+          src={src}
+          alt={alt}
+          onLoad={handleImageLoad}
+          onError={handleImageError}
+          className={`lazy-image ${imageLoaded ? 'loaded' : 'loading'}`}
+          loading="lazy" // Native lazy loading as fallback
+        />
+      )}
+    </div>
+  );
+};

--- src/components/LazyImage/LazyImage.scss
+++ src/components/LazyImage/LazyImage.scss
@@ -0,0 +1,133 @@
+.lazy-image-container {
+  position: relative;
+  width: 100%;
+  height: 100%;
+  overflow: hidden;
+  border-radius: inherit;
+}
+
+.lazy-image-placeholder {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  background: var(--surface-color);
+  border-radius: inherit;
+
+  .placeholder-image {
+    width: 100%;
+    height: 100%;
+    object-fit: cover;
+    filter: blur(5px);
+    transform: scale(1.1);
+    transition: all 0.3s ease;
+  }
+}
+
+.skeleton-placeholder {
+  position: relative;
+  width: 100%;
+  height: 100%;
+  background: var(--skeleton-base);
+  border-radius: inherit;
+  overflow: hidden;
+
+  .skeleton-shimmer {
+    position: absolute;
+    top: 0;
+    left: -100%;
+    width: 100%;
+    height: 100%;
+    background: linear-gradient(
+      90deg,
+      transparent,
+      var(--skeleton-shimmer),
+      transparent
+    );
+    animation: shimmer 1.5s infinite;
+  }
+}
+
+@keyframes shimmer {
+  0% {
+    left: -100%;
+  }
+  100% {
+    left: 100%;
+  }
+}
+
+.lazy-image-error {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  justify-content: center;
+  gap: 8px;
+  background: var(--surface-color);
+  color: var(--text-secondary);
+  border-radius: inherit;
+  padding: 16px;
+  text-align: center;
+
+  svg {
+    opacity: 0.6;
+  }
+
+  span {
+    font-size: 0.875rem;
+    font-weight: 500;
+  }
+}
+
+.lazy-image {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  object-fit: cover;
+  border-radius: inherit;
+  transition: opacity 0.3s ease;
+
+  &.loading {
+    opacity: 0;
+  }
+
+  &.loaded {
+    opacity: 1;
+  }
+}
+
+// Dark theme adjustments
+[data-theme="dark"] {
+  .lazy-image-placeholder {
+    background: var(--surface-dark);
+  }
+
+  .skeleton-placeholder {
+    background: var(--skeleton-base-dark);
+
+    .skeleton-shimmer {
+      background: linear-gradient(
+        90deg,
+        transparent,
+        var(--skeleton-shimmer-dark),
+        transparent
+      );
+    }
+  }
+
+  .lazy-image-error {
+    background: var(--surface-dark);
+    color: var(--text-secondary-dark);
+  }
+}

