import React from 'react';
import './LogoWithText.scss';
import { Logo } from './Logo';

interface LogoWithTextProps {
  size?: number;
  showSubtext?: boolean;
  className?: string;
}

export const LogoWithText: React.FC<LogoWithTextProps> = ({ 
  size = 50, 
  showSubtext = false, 
  className 
}) => {
  return (
    <div className={`logo-container ${className || ''}`}>
      <Logo size={size} />
      <div>
        <div className="brand-text">PixelVault</div>
        {showSubtext && <div className="sub-text">Secure Photo Storage</div>}
      </div>
    </div>
  );
};

