 $blur-sm: blur(8px) !default;
 $blur-md: blur(16px) !default;
 $blur-lg: blur(24px) !default;
+
+// 8bit Pixel Effects
+$pixel-border: 2px solid $pixel-blue !default;
+$pixel-border-thick: 3px solid $pixel-blue !default;
+$pixel-glow: 0 0 10px $pixel-blue, 0 0 20px rgba(102, 217, 255, 0.5) !default;
+$pixel-glow-strong: 0 0 15px $pixel-blue, 0 0 30px rgba(102, 217, 255, 0.7), 0 0 45px rgba(102, 217, 255, 0.4) !default;
+
+// 8bit Mixins
+@mixin pixel-style {
+  font-family: $font-pixel;
+  image-rendering: pixelated;
+  image-rendering: -moz-crisp-edges;
+  image-rendering: crisp-edges;
+}
+
+@mixin pixel-button {
+  @include pixel-style;
+  background: $bg-glass;
+  border: $pixel-border;
+  border-radius: $border-radius-small;
+  color: $text-primary;
+  cursor: pointer;
+  padding: 8px 16px;
+  transition: all 0.2s ease;
+  text-transform: uppercase;
+  font-weight: 600;
+  letter-spacing: 1px;
+  
+  &:hover {
+    background: $bg-glass-hover;
+    box-shadow: $pixel-glow;
+    transform: translateY(-1px);
+  }
+  
+  &:active {
+    transform: translateY(0);
+  }
+}
+
+@mixin pixel-card {
+  @include pixel-style;
+  background: $bg-glass;
+  border: 1px solid $border-glass;
+  border-radius: $border-radius;
+  backdrop-filter: $blur-sm;
+  -webkit-backdrop-filter: $blur-sm;
+  box-shadow: $shadow-glass;
+  
+  &:hover {
+    background: $bg-glass-hover;
+    box-shadow: $shadow-hover;
+    transform: translateY(-2px);
+  }
+}

