+        keyMatches = true;
+      } else if (shortcut.key === '?' && event.key === '/' && event.shiftKey) {
+        // Special case for ? which is Shift+/
+        keyMatches = true;
+      }
+
+      // Check modifier keys
+      const ctrlMatches = Boolean(shortcut.ctrlKey) === event.ctrlKey;
