+import { useEffect } from 'react';
+import './PhotoModal.scss';
+
+type Props = {
+    isOpen: boolean;
+    imageUrl: string;
+    imageName: string;
+    onClose: () => void;
+    onDelete?: () => void;
+}
+
+export const PhotoModal = ({ isOpen, imageUrl, imageName, onClose, onDelete }: Props) => {
+    useEffect(() => {
+        const handleEscape = (e: KeyboardEvent) => {
+            if (e.key === 'Escape') {
+                onClose();
+            }
+        };
+
+        if (isOpen) {
+            document.addEventListener('keydown', handleEscape);
+            document.body.style.overflow = 'hidden';
+        }
+
+        return () => {
+            document.removeEventListener('keydown', handleEscape);
+            document.body.style.overflow = 'unset';
+        };
+    }, [isOpen, onClose]);
+
+    if (!isOpen) return null;
+
+    const handleBackdropClick = (e: React.MouseEvent) => {
+        if (e.target === e.currentTarget) {
+            onClose();
+        }
+    };
+
+    const handleDownload = () => {
+        const link = document.createElement('a');
+        link.href = imageUrl;
+        link.download = imageName;
+        link.click();
+    };
+
+    return (
+        <div className="photo-modal-overlay" onClick={handleBackdropClick}>
+            <div className="photo-modal">
+                <div className="photo-modal-header">
+                    <h3 className="photo-modal-title">{imageName}</h3>
+                    <div className="photo-modal-actions">
+                        <button 
+                            className="action-btn download-btn"
+                            onClick={handleDownload}
+                            aria-label="Download image"
+                        >
+                            <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
+                                <path d="M12 16L7 11L8.41 9.59L11 12.17V4H13V12.17L15.59 9.59L17 11L12 16Z" fill="currentColor"/>
+                                <path d="M19 18H5V20H19V18Z" fill="currentColor"/>
+                            </svg>
+                        </button>
+                        {onDelete && (
+                            <button 
+                                className="action-btn delete-btn"
+                                onClick={onDelete}
+                                aria-label="Delete image"
+                            >
+                                <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
+                                    <path d="M6 7H5V5H9L10 4H14L15 5H19V7H18V19C18 20.1 17.1 21 16 21H8C6.9 21 6 20.1 6 19V7ZM8 9V19H16V9H8ZM9 11H11V17H9V11ZM13 11H15V17H13V11Z" fill="currentColor"/>
+                                </svg>
+                            </button>
+                        )}
+                        <button 
+                            className="action-btn close-btn"
+                            onClick={onClose}
+                            aria-label="Close modal"
+                        >
+                            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
+                                <path d="M19 6.41L17.59 5L12 10.59L6.41 5L5 6.41L10.59 12L5 17.59L6.41 19L12 13.41L17.59 19L19 17.59L13.41 12L19 6.41Z" fill="currentColor"/>
+                            </svg>
+                        </button>
+                    </div>
+                </div>
+                
+                <div className="photo-modal-content">
+                    <img 
+                        src={imageUrl} 
+                        alt={imageName}
+                        className="photo-modal-image"
+                    />
+                </div>
+            </div>
+        </div>
+    );
+};

